mapper.update:
UPDATE member
SET enabled = #{enabled}
WHERE username = #{username}

mapper.select:
SELECT username, name, email, phone, grade, joinday AS joinDate, enabled, LOWER(role) AS role FROM member WHERE 1=1
AND LOWER(name) LIKE '%' || LOWER(#{search}) || '%'
AND enabled = #{status}

mapper.update:
UPDATE orders
SET order_status = #{status}
WHERE order_no = #{orderId}
