mapper.update:
UPDATE cart
SET cart_ea =
COALESCE(cart_ea, 0) + 1,
cart_totalprice = cart_price *
(COALESCE(cart_ea, 0) + 1)
WHERE username = #{username}
AND item_no =
#{itemNo}
AND item_size = #{itemSize}

mapper.delete:
delete from cart
where username = #{username}

	item_no = #{item.itemNo} and item_size = #{item.itemSize}

mapper.select:
select c.item_no, i.item_irum, i.item_price, COALESCE(#{imageUrl}, '/default/images/') || ii.image_name AS item_image, c.cart_ea, c.cart_totalprice from cart c join item i on c.item_no = i.item_no join item_image ii on c.item_no = ii.item_no where c.username = #{username} and ii.image_no in
#{item}
and ii.image_no = 0
